import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("");

  const handleText = (event) => {
    setText(event.target.value);
  };

  const handleClickUp = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleClickLo = () => {
    let newText = text.toLowerCase();
    setText(newText);
    };
    

  return (
    <>
      <div className="container my-3">
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            id="myBox"
            rows="8"
            placeholder="Enter your text here"
            value={text}
            onChange={handleText}
          ></textarea>
          <button className="btn btn-primary my-2 mx-1" onClick={handleClickUp}>
            Convert to Uppercase
          </button>
          <button className="btn btn-primary my-2 mx-1" onClick={handleClickLo}>
            Convert to Lowercase
          </button>
          <button className="btn btn-primary my-2 mx-1" onClick={handleClear}>
            Clear
          </button>
        </div>
      </div>

      <div className="container my-3">
        <h2>Your Text Analysis</h2>
        <div className="mb-3">
                  <p>{text.split(" ").length - 1} words and {text.length} characters</p>
                  <p>{Math.round((text.split(" ").length - 1) * 0.4)} seconds to read.</p>
                  <h1>Preview</h1>
                  <p>{text}</p>
        </div>
      </div>
    </>
  );
}
