import React, {useState} from "react";

export default function TextForm(props) {

    const [text, setText] = useState("");

    const handleText = (event) => {
        setText(event.target.value);
    }

    const handleClick = () => {
        let newText = text.toUpperCase();
        setText(newText);
    }

    return (
      <>
    <div className="container my-3">
          <div className="mb-3">
              <h1>{ props.heading}</h1>
              <textarea className="form-control" id="myBox" rows="8" placeholder="Enter your text here" value={text} onChange={handleText}></textarea>
              <button className="btn btn-primary my-2" onClick={handleClick}>Convert to Uppercase</button>
          </div>
            </div>
            </>        
  );
}
