    <div className="container my-3">
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" id="myBox" rows="8" placeholder=""></textarea>
          </div>
          <button className="btn btn-primary">Convert to Uppercase</button>
    </div>
  );
}
