import React from "react";

export default function TextForm(props) {

  return (
    <div className="container my-3">
          <div className="mb-3">
              <h1>{ props.heading}</h1>
              <textarea className="form-control" id="myBox" rows="8" placeholder="Enter your text here"></textarea>
              
          </div>
    </div>  
  );
}
