import React from "react";

export default function TextForm(props) {
  return (
    <div className="container">
      <div className="mb-3 my-3">
        <h1>{props.heading}</h1>
        <textarea class="form-control" id="myBox" rows="8"></textarea>
      </div>
    </div>
  );
}
